import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";

class MovieForm extends Form {
  state = {
    data: { title: "", genre: "", numberInStock: null, rating: null },
    errors: {},
  };

  schema = {
    title: Joi.string()
      .required()
      .label("Title"),
    genre: Joi.string()
      .required()
      .label("Genre"),
    stock: Joi.number()
      .required()
      .label("Stock")
      .integer()
      .min(0),
    rating: Joi.number()
      .required()
      .label("Rating")
      .min(0)
      .max(10),
  };

  renderExisting = ({ match, history }) => {
    return (
      <div>
        <h1>Movie Form {match.params.id}</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderInput("genre", "Genre", "password")}
          {this.renderInput("numberInStock", "Stock", "number")}
          {this.renderInput("rating", "Rating", "float")}
        </form>
        <button
          className="btn btn-primary"
          onClick={() => history.push("/movies")}
        >
          Back
        </button>
      </div>
    );
  };

  addNew = () => {
    return (
      <div>
        <h1>Add a movie</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderInput("genre", "Genre", "password")}
          {this.renderInput("numberInStock", "Stock", "number")}
          {this.renderInput("rating", "Rating", "float")}
        </form>
        {this.renderButton("Save")}
      </div>
    );
  };

  doSubmit = () => {
    // do this if form data is valid
    console.log("Login submitted");
  };

  render() {
    return movie.existing ? this.renderExisting() : this.addNew();
  }
}
